import debounce from 'debounce';
import { SeoAssessor, Paper, interpreters, string } from 'yoastseo';
import { getAssessorRatings, getI18n } from './utils.js';
import { mapGetters, mapMutations } from 'vuex';
const removeHtmlBlocks = string.removeHtmlBlocks;
const scoreToRating = interpreters.scoreToRating;
const ratings = getAssessorRatings();
export default {
  props: {
    title: {
      type: String,
      default: ''
    },
    titleWidth: {
      type: Number,
      default: 100
    },
    description: {
      type: String,
      default: ''
    },
    keyword: {
      type: String,
      default: ''
    },
    url: {
      type: String,
      default: ''
    },
    text: {
      type: String,
      default: ''
    },
    locale: {
      type: String,
      default: 'en_US'
    },
    marker: {
      type: [Object, null],

      default() {
        return null;
      }

    },
    permalink: {
      type: String,
      default: ''
    },
    translations: {
      type: Object,

      default() {
        return null;
      }

    },
    resultFilter: {
      type: Function,

      default(result) {
        return result;
      }

    }
  },

  data() {
    return {
      assessorResults: [],
      assessorResultsByRating: {},
      responseData: null,
      overallSeoScore: null,
      overallSeoRating: null,
      seoAssessor: null
    };
  },

  computed: {
    items() {
      const res = [];
      ratings.forEach(rating => {
        const items = this.assessorResultsByRating[rating];
        if (typeof items === 'undefined') return;
        items.forEach(item => {
          if (item.text.length === 0) return;
          item._class = ['vue-yoast-assessor-item', 'vue-yoast-assessor-item-' + item.rating];
          res.push(item);
        });
      });
      return res;
    }

  },
  watch: {
    title(newVal, oldVal) {
      if (newVal !== oldVal) this._debouncedRefresh();
    },

    titleWidth(newVal, oldVal) {
      if (newVal !== oldVal) this._debouncedRefresh();
    },

    description(newVal, oldVal) {
      if (newVal !== oldVal) this._debouncedRefresh();
    },

    keyword(newVal, oldVal) {
      if (newVal !== oldVal) this._debouncedRefresh();
    },

    url(newVal, oldVal) {
      if (newVal !== oldVal) this._debouncedRefresh();
    },

    text(newVal, oldVal) {
      if (newVal !== oldVal) this._debouncedRefresh();
    },

    translations(newVal) {
      this.i18n = getI18n(newVal);
      this.refresh();
    },

    locale(newVal, oldVal) {
      if (newVal !== oldVal) this._debouncedRefresh();
    },

    overallSeoScore() {
      this.$emit('update:overallScore', {
        score: this.overallSeoScore,
        rating: this.overallSeoRating
      });
    }

  },

  created() {
    this._debouncedRefresh = debounce(this.refresh, 1000);
  },

  mounted() {
    this.i18n = getI18n(this.translations);
    this.refresh();
  },

  methods: { ...mapMutations(['addResponse']),
    ...mapGetters(['getRes']),

    addToResponse() {
      this.addResponse(this.responseData);
    },

    refreshPaper() {
      const text = removeHtmlBlocks(this.text);
      this.paper = new Paper(text, {
        keyword: this.keyword,
        description: this.description,
        url: this.url,
        title: this.title,
        titleWidth: this.titleWidth,
        locale: this.locale,
        permalink: this.permalink,
        link: 'https://beautyfulls.com'
      });
    },

    refresh() {
      this.refreshPaper();
      this.seoAssessor = new SeoAssessor(this.i18n, {
        marker: this.marker
      });
      this.seoAssessor.assess(this.paper);
      this.overallSeoScore = this.seoAssessor.calculateOverallScore();
      this.overallSeoRating = scoreToRating(this.seoAssessor.calculateOverallScore() / 10);
      this.assessorResults = [];
      this.assessorResultsByRating = {};
      this.seoAssessor.results.forEach(item => {
        if (item.text.includes('Tiêu đề SEO quá ngắn')) {
          let text = '';
          let score = 0;

          if (this.title.length >= 50 && this.title.length <= 70) {
            text = '<a href="https://yoa.st/34h" target="_blank">Độ rộng tiêu đề SEO</a>: Tiêu đề SEO khoảng 50 đến 70 từ, rất tốt.';
            score = 9;
          } else if (this.title.length > 70) {
            text = '<a href="https://yoa.st/34h" target="_blank">Độ rộng tiêu đề SEO</a>: Độ rộng tiêu đề SEO quá dài : ' + this.title.length + ' kí tự, những từ phía sau sẽ không hiển thị được, sẽ xuất hiện dấu ba chấm (…) ở phía sau';
            score = 6;
          } else if (this.title.length < 50 && this.title.length !== 0) {
            text = '<a href="https://yoa.st/34h" target="_blank">Độ rộng tiêu đề SEO</a>: Độ rộng tiêu đề SEO quá ngắn, không đủ 50 kí tự';
            score = 3;
          } else {
            text = '<a href="https://yoa.st/34h" target="_blank">Độ rộng tiêu đề SEO</a>: Không được để trống tiêu đều SEO';
            score = 0;
          }

          const result = this.resultFilter({
            rating: scoreToRating(score),
            socre: score,
            text: text
          });
          this.assessorResults.push(result);

          if (this.assessorResultsByRating.hasOwnProperty(result.rating)) {
            this.assessorResultsByRating[result.rating].push(result);
          } else {
            this.assessorResultsByRating[result.rating] = [result];
          }
        } else {
          const result = this.resultFilter({
            rating: scoreToRating(item.score),
            socre: item.score,
            text: item.text
          });
          this.assessorResults.push(result);

          if (this.assessorResultsByRating.hasOwnProperty(result.rating)) {
            this.assessorResultsByRating[result.rating].push(result);
          } else {
            this.assessorResultsByRating[result.rating] = [result];
          }
        }
      });
      this.responseData = this.keyword + '/' + this.seoAssessor.calculateOverallScore() / 10;
      this.addToResponse(this.responseData); // this.getRes()

      setTimeout(this.getRes, 2000);
      this.$emit('update:results', this.assessorResults);
      this.$emit('update:resultsByRating', this.assessorResultsByRating);
    }

  }
};
